INSERT OR REPLACE INTO EnglishText (Tag,Text) VALUES
('LOC_IMPROVEMENT_ZIGGURAT_DESCRIPTION_ZJ',
'Unlocks the Builder ability to construct a Ziggurat, unique to Sumeria.[NEWLINE][NEWLINE]+2 [ICON_Science] Science. +1 [ICON_Culture] Culture if next to River. Cannot be built on Hills but may be built on Floodplains. Additional 1 [ICON_Culture] Culture and +1 [ICON_Science] Science once Education is discovered.'),

('LOC_TRAIT_CIVILIZATION_FIRST_CIVILIZATION_DESCRIPTION_ZJ', 
'When you disperse a Barbarian Outpost, receive a Tribal Village reward in addition to the usual Experience Points. Pay half the usual cost to levy city-state units. +4 [ICON_Strength] Unit Combat Strength when fighting Barbarians.'),

('LOC_IMPROVEMENT_PYRAMID_DESCRIPTION_ZJ', --Nubia
'Improvement that unlocks with Masonry and must be built on Desert, Desert Hills or Desert Floodplains. +1 [ICON_Faith] Faith. Receives additional yields from adjacent districts. +1 [ICON_Food] Food if adjacent to a city center. Increase of +1 to the adjacency bonuses being earned by any adjacent district.'),
('LOC_DISTRICT_PYRAMID_FAITH_ZJ',
'+1 [ICON_Faith] Faith from the adjacent Nubian Pyramid.'),
('LOC_DISTRICT_PYRAMID_SCIENCE_ZJ',
'+1 [ICON_Science] Science from the adjacent Nubian Pyramid.'),
('LOC_DISTRICT_PYRAMID_GOLD_ZJ',
'+1 [ICON_Gold] Gold from the adjacent Nubian Pyramid.'),
('LOC_DISTRICT_PYRAMID_PRODUCTION_ZJ',
'+1 [ICON_Production] Production from the adjacent Nubian Pyramid.'),
('LOC_DISTRICT_PYRAMID_CULTURE_ZJ',
'+1 [ICON_Culture] Culture from the adjacent Nubian Pyramid.'),

('LOC_TRAIT_CIVILIZATION_NOBEL_PRIZE_DESCRIPTION_ZJ', --Sweden
'Sweden gains 50 [ICON_FAVOR] Diplomatic Favor when earning a Great Person (on Standard Speed). Sweden receives +2 [ICON_GREATENGINEER] Great Engineer point from Factories, +2 [ICON_GREATARTIST] Great Artist point from Factories, and +2 [ICON_GREATSCIENTIST] Great Scientist point from Universities. Having Sweden in the game adds three unique World Congress competitions starting in the Industrial Era.'),

('LOC_DISTRICT_NUSANTARA_GOLD_ZJ', --Indonesia
'+{1_num} [ICON_Gold] Gold from the adjacent Coast or Lake {1_Num : plural 1?tile; other?tiles;}.'),
('LOC_CITY_CENTER_COAST_FAITH_ZJ',
'+{1_num} [ICON_Faith] Faith from the adjacent Coast or Lake {1_Num : plural 1?tile; other?tiles;}.'),
('LOC_CITY_CENTER_COAST_FOOD_ZJ',
'+{1_num} [ICON_Food] Food from the adjacent Coast or Lake {1_Num : plural 1?tile; other?tiles;}.'),
('LOC_CITY_CENTER_COAST_PRODUCTION_ZJ',
'+{1_num} [ICON_Production] Production from the adjacent Coast or Reef {1_Num : plural 1?tile; other?tiles;}.'),

('LOC_TRAIT_CIVILIZATION_SCOTTISH_ENLIGHTENMENT_DESCRIPTION_ZJ', --Scoland
'Happy cities receive an additional +10% [ICON_Science] Science and +10% [ICON_Production] Production. Happy cities generate +1 [ICON_GreatScientist] Great Scientist point per Campus and +1 [ICON_GreatEngineer] Great Engineer point per Industrial Zone. Ecstatic cities double all these amounts.'),
('LOC_IMPROVEMENT_GOLF_COURSE_XP2_DESCRIPTION_ZJ',
'Unlocks the Builder ability to construct a Golf Course, unique to Scotland.[NEWLINE][NEWLINE]+2 [ICON_Amenities] Amenities and +4 [ICON_Gold] Gold, +2 [ICON_Culture] Culture. +1 [ICON_Culture] Culture if adjacent to a City Center district and +1 [ICON_Culture] Culture if adjacent to an Entertainment Complex district. Additional [ICON_Tourism] Tourism and [ICON_Housing] Housing as you advance through the Civics and Technology Tree. One per city. Tiles with Golf Courses cannot be swapped. +1 Appeal.'),

('LOC_IMPROVEMENT_SPHINX_DESCRIPTION_ZJ', --Egypt
'Unlocks the Builder ability to construct a Sphinx, unique to Egypt.[NEWLINE][NEWLINE]+2 [ICON_Faith] Faith and +2 [ICON_Culture] Culture. +2 Appeal. +2 [ICON_Faith] Faith and +1 [ICON_Culture] Culture if next to a wonder. +1 [ICON_Culture] Culture if built on Floodplains. Additional 2 [ICON_Culture] Culture once Natural History is discovered. Provides [ICON_Tourism] Tourism after researching Flight. Cannot be built next to another Sphinx. Cannot be built on Snow or Snow Hills.'),
('LOC_TRAIT_CIVILIZATION_ITERU_EXPANSION2_DESCRIPTION_ZJ',
'+50% [ICON_Production] Production towards wonders if placed next to a River. Wonders before medieval age give one [ICON_TradeRoute] trade route capacity and a free trader. Does not receive damage from Floods.'),

('LOC_TRAIT_CIVILIZATION_SATRAPIES_DESCRIPTION_ZJ', --Persia
'+1 [ICON_TradeRoute] Trade Route capacity with Political Philosophy civic. Receive +2 [ICON_Gold] Gold and +1 [ICON_Culture] Culture for routes between your own cities. +1 [ICON_Culture] Culture for each Pairidaeza in the destination city. Roads built in your territory are one level more advanced than usual.'),
('LOC_TRAIT_LEADER_FALL_BABYLON_EXPANSION2_DESCRIPTION_ZJ',
'+1 [ICON_Production] Production per [ICON_Citizen] Citizen in cities with [ICON_Governor] Governors. Yield more Production after researching Apprenticeship and Industrialization.'),
('LOC_IMPROVEMENT_PAIRIDAEZA_DESCRIPTION_ZJ',
'+1 [ICON_Culture] Culture and +2 [ICON_Gold] Gold. +2 Appeal. +1 [ICON_Culture] Culture for each adjacent Holy Site and Theater Square. +1 [ICON_Gold] Gold for each adjacent Commercial Hub and City Center. Additional [ICON_Culture] Culture and [ICON_Tourism] Tourism as you advance through the Technology and Civics Tree. Cannot be built on Snow, Tundra, Snow Hills, or Tundra Hills or adjacent to another Pairidaeza. When a player constructs their first Pairidaeza receive one governor title.'),

('LOC_TRAIT_CIVILIZATION_MEDITERRANEAN_COLONIES_DESCRIPTION_ZJ', --Phoenicia
'Begin the game with the Writing technology [ICON_TECHBOOSTED] Eureka and Foreign Trade [ICON_CIVICBOOSTED] Inspiration. Receive 1 [ICON_ENVOY] Envoy every times you founded a coastal city (Exclude Capital City). Coastal cities founded by Phoenicia and located on the same continent as the Phoenician [ICON_CAPITAL] Capital are 100% Loyal. Settlers gain +2 [ICON_MOVEMENT] Movement and +2 sight range while embarked. Settlers ignore additional [ICON_MOVEMENT] Movement costs from embarking and disembarking.'),

('LOC_TRAIT_LEADER_TRAJANS_COLUMN_DESCRIPTION_ZJ', --Leader TRAJANS
'All cities start with an additional City Center building. (Starts with a Monument building in the Ancient era). Monument Grant [ICON_Production] Production, [ICON_Gold] Gold, and [ICON_Faith] Faith when unlock lv.2 Goverment Civs.'),

('LOC_TRAIT_CIVILIZATION_MALI_GOLD_DESERT_DESCRIPTION_ZJ',
'City Centers gain +1 [ICON_Faith] Faith and +1 [ICON_Food] Food for every adjacent Desert and Desert Hills tiles. Mines receive -1 [ICON_Production] Production and +4 [ICON_Gold] Gold. May purchase Commercial Hub and Goverment Plaza district buildings with [ICON_Faith] Faith. -30% [ICON_Production] Production toward constructing buildings or training units.'),

('LOC_TRAIT_LEADER_SAHEL_MERCHANTS_DESCRIPTION_ZJ', --Leader Mansa
'International [ICON_TRADEROUTE] Trade Routes gain +1 [ICON_Gold] Gold for every flat Desert tile in the origin city. Receive +1 [ICON_TRADEROUTE] Trade Capacity first time you enter a Golden Age. +2 [ICON_Gold] Gold from Desert.'),

('LOC_DISTRICT_SUGUBA_DESCRIPTION_ZJ',
'A district unique to Mali specializing in finance and trade that replaces the Commercial Hub. Tiles, Units, Buildings, and Districts are 20% cheaper to purchase with [ICON_Gold] Gold and [ICON_Faith] Faith in this City.[NEWLINE][NEWLINE]+2 [ICON_Gold] Gold bonus for each adjacent Holy Site. +2 [ICON_Gold] Gold bonus from a tile containing a River edge. +1 [ICON_Gold] Gold bonus for every two adjacent district tiles.'),

('LOC_TRAIT_CIVILIZATION_WONDER_TOURISM_DESCRIPTION_ZJ', --France
'+20% [ICON_Production] Production toward Medieval, Renaissance, and Industrial era wonders. [ICON_Tourism] Tourism from wonders of any era is +100%. Receives a free Spy (and extra spy capacity) with the Code of Law civic. Build Theater, Diplomatic Quarter and their building in half of the time.'),

('LOC_TRAIT_LEADER_FLYING_SQUADRON_DESCRIPTION_ZJ', --black queen
'Has 1 level of [ICON_VISLIMITED] Diplomatic Visibility greater than normal with every civilization that she has met. +2 sight range for spies and traders. All spies start with a free promotion. Spies take no time to establish their presence. +2 Combat Strength for all units for each level of [ICON_VISLIMITED] Diplomatic Visibility France has over the other civilization, instead of the usual +3 Combat Strength.'),

('LOC_TRAIT_LEADER_FLYING_SQUADRON_DESCRIPTION_ZJ', --black queen
'Improved Luxury resources adjacent to Theater Square districts or Ch√¢teaux receive +2 [ICON_Culture] Culture. May initiate the Court Festival project in any city with a Theater Square district. Theater Square can trigger culture boom.'),
('LOC_TECH_CASTLES_DESCRIPTION_ZJ',
'Bonus [ICON_Culture] Culture for the Great Wall improvement for adjacency.'),

('LOC_TRAIT_CIVILIZATION_INDONESIA_NUSANTARA_DESCRIPTION_ZJ', 
'Coast and Lake tiles provide a standard adjacency bonus for Holy Site, Campus, Industrial Zone, and Theater Square districts. +1 [ICON_Amenities] Amenity from entertainment to each Entertainment Complex adjacent to a Coast or Lake tile. Celestial Navigation technologies will unlock Shipbuilding technologie.'),

('LOC_TRAIT_LEADER_EXALTED_GODDESS_DESCRIPTION_ZJ', 
'Naval units can be purchased with [ICON_Faith] Faith. Religious units pay no movement to embark or disembark. City Centers gain +1 [ICON_Faith] Faith and +1 [ICON_Food] Food for every adjacent Coast and Lake tiles, +2 production for every adjacent reef.'),

('LOC_TRAIT_CIVILIZATION_GOLDEN_AGE_QUESTS_DESCRIPTION_ZJ', --Georgia
-- Receives a permanent extra Wildcard Policy Slot once if enters a Golden Age or Heroic Age for the first time.
'Begin the game with the Masonry technology [ICON_TECHBOOSTED] Eureka. When making Dedications at the beginning of a Golden Age or Heroic Age, receive the Normal Age bonus towards improving Era Score in addition to the other bonus. May use Dark Age Policies during a Golden Age or Heroic Age. All the cities you build will grant the highest level wall.'),
('LOC_BUILDING_TSIKHE_DESCRIPTION_XP2_ZJ',
'A building unique to Georgia. Higher outer defenses than Renaissance Walls. Provides +3 [ICON_Tourism] Tourism after advancing to the Conservation Civic. Provides +4 [ICON_Faith] Faith, +2 [ICON_Culture] Culture. When in a Golden Age the [ICON_Tourism] Tourism, [ICON_Faith] Faith and [ICON_Culture] Culture are +100%.'),

('LOC_TRAIT_CIVILIZATION_FACES_OF_PEACE_DESCRIPTION_ZJ', --Canada
'Cannot declare war on City-States or surprise wars. Surprise wars cannot be declared on Canada. For every 100 [ICON_Tourism] Tourism per turn earn 1 [ICON_FAVOR] Diplomatic Favor per turn. Receive +100% [ICON_FAVOR] Diplomatic Favor from successfully completing an Emergency or Scored Competition. National Park +100% [ICON_Tourism] Tourism. Your cities earn +15% [ICON_Culture] Culture whenever you are not at war with any civilization.'),


('LOC_TRAIT_LEADER_MENELIK_DESCRIPTION_ZJ',
'Receive [ICON_Science] Science and [ICON_Culture] Culture equal to 20% of your [ICON_Faith] Faith generation in cities founded on Hills. Units receive +4 [ICON_Strength] Combat Strength on Hills.'),

('LOC_TRAIT_CIVILIZATION_INDUSTRIAL_REVOLUTION_DESCRIPTION_ZJ', --England
'Iron, Coal and Aluminum Mines accumulate 2 more resources per turn. +100% [ICON_Production] Production towards Military Engineers. Military Engineers receive +2 charges. Buildings that provide additional yields when [ICON_POWER] Powered receive +5 of that yield. +25% [ICON_Production] Production towards Industrial Zone buildings. Harbor buildings increase Strategic Resource Stockpiles by +10 (on Standard Speed). Reveals [ICON_RESOURCE_COAL] Coal by Apprenticeship, and reveals [ICON_RESOURCE_ALUMINUM] Aluminum by industrialization'),

('LOC_TRAIT_CIVILIZATION_GROTE_RIVIEREN_DESCRIPTION_ZJ',--Neitherlands
'Major adjacency bonus for Campuses, Theater Squares, and Industrial Zones if next to a river. Culture Bomb adjacent tiles when completing a Harbor. +50% [ICON_Production] Production toward the Dam district and Flood Barrier building. Dams are unlocked with the Masonry technology.'),

('LOC_TRAIT_RADIO_ORANJE_DESCRIPTION_ZJ', --Wilhelmina
'Your [ICON_TRADEROUTE] Trade Routes to your own cities provide +1 Loyalty per turn for the starting city. [ICON_TRADEROUTE] Trade Routes to foreign cities or from foreign cities provide +1 [ICON_Culture] Culture to you. Your cities earn +15% [ICON_Gold] Gold whenever you are not at war with any civilization.'),

('LOC_TRAIT_LEADER_KUBLAI_DESCRIPTION_ZJ', --leader KUBLAI
'One extra Economic policy slot in any government. Receive a random [ICON_CIVICBOOSTED] Eureka and [ICON_TECHBOOSTED] Inspiration when establishing a [ICON_TradingPost] Trading Post in another Civilizations city for the first time. Each Encampment buildings yield 5 [ICON_Gold] Gold, Encampment +1 [ICON_TRADEROUTE] Trade Route capacity.'),

('LOC_TRAIT_LEADER_MUTAL_DESCRIPTION_ZJ', --Maya
'Non capital cities within 6 tiles of the [ICON_CAPITAL] Capital gain +20% to all yields. Other non capital cities receive -20% to all yields. +7 [ICON_Strength] Combat Strength to units within 6 tiles of the [ICON_CAPITAL] Capital.'),

('LOC_CIVILIZATION_LAST_PROPHET_DESCRIPTION_ZJ', --Arabian(Saladin)
"Automatically receive the final [ICON_GREATPROPHET] Great Prophet when the next-to-last one is claimed (if you have not earned a [ICON_GREATPROPHET] Great Prophet already). +1 [ICON_Science] Science for each foreign city following Arabia's Religion. Holy Site give Culture Bomb."),

('LOC_DISTRICT_HANSA_GOLD_ZJ', --Germany
'+{1_num} [ICON_Gold] Gold from the adjacent Hansa {1_Num : plural 1?tile; other?district;}.'),

('LOC_CIVILIZATION_EXTRA_LIGHT_CAVALRY_DESCRIPTION_ZJ', --Scythia
'Receive a second light cavalry unit or Saka Horse Archer each time you train a light cavalry unit or Saka Horse Archer. Reveals [ICON_RESOURCE_HORSES] Horse from beginning.'),

('LOC_CIVILIZATION_FOUNDING_FATHERS_DESCRIPTION_ZJ',
'All Diplomatic policy slots in the current government are converted to Wildcard slots. +1 [ICON_FAVOR] Diplomatic Favor per turn for every Wildcard slot in their government. Reduces the cost of purchasing a tile by 25%.'),

('LOC_CIVILIZATION_PEARL_DANUBE_DESCRIPTION_ZJ', --Hungary
'+50% [ICON_Production] Production to Districts and Buildings constructed across a river from a City Center. The city build on Geothermal Fissure +2 [ICON_Food] Food , +2 [ICON_Production] Production, +1 [ICON_Housing] Housing. Constructs the first Sukiennice grant a free trader.'),

('LOC_TRAIT_CIVILIZATION_GOLDEN_LIBERTY_DESCRIPTION_ZJ', --Poland
"Culture Bomb adjacent tiles when completing an Encampment or Fort inside friendly territory. One Military policy slot in the current government is converted to a Wildcard slot. Districts placed on Grass or Plains +1 [ICON_HOUSING] house. Constructs first Sukiennice receive [ICON_Gold] Gold that equl to it's cost."),
('LOC_BUILDING_SUKIENNICE_EXPANSION1_DESCRIPTION_ZJ',
'A building unique to Poland. International [ICON_TRADEROUTE] Trade Routes from this city gain +2 [ICON_Production] Production; domestic [ICON_TRADEROUTE] Trade Routes gain +4 [ICON_Gold] Gold. Bonus [ICON_Production] Production equal to the adjacency bonus of the Commercial Hub district. +1 [ICON_TRADEROUTE] Trade Route capacity, if this city does not yet have a Lighthouse building.');



